sys_variable_value.value:
(function execute(inputs, outputs) {

  var parmObj = {};
  parmObj['status'] = inputs['status'];
  parmObj['doc_name'] = inputs['doc_name'];
  parmObj['doc_owner'] = inputs['doc_owner'];
  parmObj['company'] = inputs['company'];
  parmObj['document_id'] = inputs['document_id'];
  parmObj['approver'] = inputs['approver'];
  parmObj['approver_id'] = inputs['approver_id'];
  if(inputs['reason'])
  	parmObj['reason'] = inputs['reason'];

  var parmStr = JSON.stringify(parmObj);

  gs.eventQueue('x_480910_test_app.notification_event',null,inputs['email_id'],parmStr);


})(inputs, outputs);

